import java.security.*;
import java.security.spec.InvalidKeySpecException;
import java.security.spec.PKCS8EncodedKeySpec;
import java.security.spec.X509EncodedKeySpec;
import java.util.Base64;

def invoke(msg)         {           
	//String privateKey = msg.get("PrivateKey").replaceAll("\\s", "")
	String privateKey = msg.get("PrivateKey").toString()
	String payload = msg.get("StringToVerify").toString()
	String hash = sign(payload, privateKey)
	//def encodedData = hash.encodeBase64().toString()
	String editedEncodeData = hash
	msg.put("encodeSignature", editedEncodeData)
 return true;         }

	private static String sign(String textPayload, String privateKey)
			throws NoSuchAlgorithmException,
			InvalidKeySpecException,
			InvalidKeyException,
			SignatureException {

		PrivateKey privateKeyObject = getPrivateKey(privateKey);
		Signature signatureProcessor = Signature.getInstance("SHA256withRSA");
		signatureProcessor.initSign(privateKeyObject);
		signatureProcessor.update(textPayload.getBytes());

		byte[] signature = signatureProcessor.sign();

		return new String(Base64.getEncoder().encode(signature));
	}

	private static PrivateKey getPrivateKey(String privateKey) throws NoSuchAlgorithmException,
			InvalidKeySpecException {

		byte[] key = Base64.getDecoder().decode(privateKey.getBytes());
		PKCS8EncodedKeySpec pkcs8EncodedKeySpec = new PKCS8EncodedKeySpec(key);
		KeyFactory keyFactory = KeyFactory.getInstance("RSA");

		return keyFactory.generatePrivate(pkcs8EncodedKeySpec);
	}

import com.vordel.mime.HeaderSet;
import com.vordel.trace.Trace;
 

def invoke(msg) {
	try {
		HeaderSet initialheaders = new HeaderSet(msg.get("http.headers"));
		String getTimestamp = initialheaders.getHeader("X-TIMESTAMP")
		String getClientId = initialheaders.getHeader("X-CLIENT-KEY")
		String getPrivKey = initialheaders.getHeader("PRIVATE-KEY")

		msg.put("rts_time", getTimestamp);
		msg.put("rts_client_id", getClientId);
		msg.put("private_key", getPrivKey);
	} catch (Exception e) {
		Trace.info("Error Header: " + e);
		return false;
	}
return true;        
}

import java.nio.charset.StandardCharsets;
import javax.crypto.Mac;
import javax.crypto.spec.SecretKeySpec;
import com.vordel.trace.Trace

def invoke(msg){        
	try {
		def secret=msg.get("client_secret");
		Trace.info("secret: " + secret)
		def message=msg.get("auth.msg");
		Trace.info("message: " + message)
		Mac sha512_HMAC = Mac.getInstance("HmacSHA512");
		Trace.info("sha512_HMAC: " + sha512_HMAC)
		SecretKeySpec secret_key = new SecretKeySpec(secret.getBytes(StandardCharsets.UTF_8), "HmacSHA512");
		sha512_HMAC.init(secret_key);
		Trace.info("sha512_HMAC: " + sha512_HMAC)
		String result = "";
		for (final byte element : sha512_HMAC.doFinal(message.getBytes())) {
			result += Integer.toString((element & 0xff) + 0x100, 16).substring(1);
		}
		Trace.info("result: " + result)
		msg.put("auth.result", result);
		return true;    
	}catch(Exception e){
		return false;
	}
}

