import com.vordel.trace.Trace;
import groovy.json.JsonOutput

def invoke(msg) {
  try {
    def inputString = msg.get("reqReversalVLK");

    // Replace '=' with ':' and add double quotes for every string
def jsonString = inputString.replaceAll(/(\w+)=([^,}]+)/) { _, key, value ->
    "\"$key\":\"$value\""
}

// Convert the modified string to a map
def jsonObject = [:]
jsonString.eachMatch(/"(\w+)":"([^"]+)"/) { match ->
    jsonObject[match[1]] = match[2]
}

// Convert the map to JSON format
def jsonOutput = JsonOutput.toJson(jsonObject)

    Trace.info("jsonOutput =" + jsonOutput);

    msg.put("formattedReqRevVLK", jsonOutput);

    return true;
  } catch (Exception e) {
    Trace.error("error =" + e);
    return false;
  }
}

